import React from "react";
import axios from "axios";
var outgoingMessage;


class Forminp extends React.Component {
    state = { companyName: "" };




    updateHistory = (event) => {
        event.preventDefault();
        var new_history = this.props.history;
        // Just created a file bound variable to use with the getData() method
        outgoingMessage = event.target[0].value;
        new_history.push({ user: "customer1", message: outgoingMessage });
        this.props.history.push({ user: "customer1", message: outgoingMessage });
        this.setState({ companyName: "" });
        super.setState({ history: new_history });



        axios.post('http://localhost:8080/customerMessage', {
            incomingMessage: outgoingMessage
        })
            .then((response) => {
                console.log(response);
            }, (error) => {
                console.log(error);
            });

    };

    render() {

        return (

            <div className="tc">

                <form onSubmit={this.updateHistory}>
                    <input
                        className="bg-light-green dib br2 pa3 ma2 shadow-5 tc"
                        style={{ width: "700px" }}
                        type="text"
                        value={this.state.companyName}
                        onChange={event => this.setState({ companyName: event.target.value })}
                        placeholder="Enter Text here"
                        required
                    />
                    <button
                        className="bg-light-green dib br2 pa3 ma2 shadow-5 tc"
                        type="submit"
                    >Send</button>
                </form>
            </div>
        );
    }
}

export default Forminp;
