import React from "react" ; 
import Message from "./Message" ; 
import ChatHistory from "./ChatHistory" ; 
import FormInp from "./FormInp" ; 

class Chatbox extends React.Component{

    state = {companyName: "" ,
             stored_value : "Your Message will appear hear",
             history : [{user : "Admin" , message : " Test message is here"},
                        ]} ; 


    updateHistory = (event) => {
        event.preventDefault() ; 
        //const new_history = this.state.history + {user : "customer1" , message : event.target[0].value}} ; 
        this.setState({history : history.pushState({user : "customer1" , message : event.target[0].value})}) ; 
      } ; 


   render(){
       return(
           
           <div className="ba bw2 pa2 bg-light-yellow" >
               <ChatHistory history={this.state.history}/>
               <FormInp onSubmit={this.updateHistory}/>
           </div>
       ) ; 
   }
}

export default Chatbox;
