import React from "react" ; 


class Login extends React.Component{
    state = {userName: "",
                password: "" } ; 
   render(){

       return(
           
           <div className=" FormInput ba br3 pa2  " >

                <form className="tc" onSubmit={ this.props.onSubmit}>
                    <input 
                        className="bg-light-green dib br2 pa3 ma2 shadow-5 tc"
                        style={{width:"200px"}}
                        type="text" 
                        value={this.state.userName}
                        onChange={event => this.setState({ userName: event.target.value })}
                        placeholder="Username" 
                        required 
                    />
                    <input 
                        className="bg-light-green dib br2 pa3 ma2 shadow-5 tc"
                        style={{width:"200px"}}
                        type="text" 
                        value={this.state.password}
                        onChange={event => this.setState({ password: event.target.value })}
                        placeholder="Password" 
                        required 
                    />

                <select label="Query Type"
                        placeholder = ""
                        options={['Insurance', "cards", "mortgage", "investments", "loans", "General"]}
               />

                    <button 
                    className="bg-light-green dib br2 pa3 ma2 shadow-5 tc"
                    type="submit"
                    >Send</button>
                </form>


               

           </div>
       ) ; 
   }
}

export default Login;
