import React from 'react';
import './App.css';
import Chatbox from "./chat_ui/Chatbox" ; 
import {state_out} from "./state_out" ; 
import "tachyons" ; 
import Login from './login/Login';
import { wait } from '@testing-library/react';



class App extends React.Component{
  constructor(){
      super() ; 
      
      this.state = {
          stored_value : "Your Message will appear hear",
          incoming : state_out.incoming_value, 
          history :  [{user : "Admin" , message : " Test message is here"},],
          loggedin : "no" , 
          }

      this.userInfo = {
        userName : "" , 
        password : "", 
        queryType : "", 
      }    

  }

  updateHistory = (event) => {
    event.preventDefault() ; 
    this.setState({stored_value : event.target[0].value}) ;  
  } ; 

  login = (user, pass, query) => {
    this.setState({loggedin : "yes"}) ; 
    this.setState({userName : user}) ; 
    this.setState({password : pass}) ; 
    this.setState({queryType : query}) ;
    console.log( this.userInfo.userName + "has logged in ") ; 
    this.render() ; 
    // create a call to routing engine 
    // log in 
    // load chat history 
    
  } ; 

  renderApp = () => {
    if (this.state.loggedin === "no"){
      console.log("not logged in ") ; 
      return (
        <div >
            <p>{this.state.loggedin}</p>
            <p className="mainText tc">Alcatel  Routing  Engine</p>
            <div className="pl4 pr4"><Login  onSumbit={this.login} /></div>

        </div>
        ) ; 
    } else {
      console.log("loggedin") ; 
      return (
        
          <div >
            <p className="mainText tc">Alcatel  Routing  Engine</p>
            <h2 className=" pa3 ma2 pl5">Assigned Admin = </h2>
            <div className="pl5 pr5"><Chatbox history={this.state.history} onMessage={this.updateHistory}/></div>
          </div>
        );
     }
    }
 


  render(){
    
    return (
        this.renderApp() 
      ) ;  
    }
  }


export default App;

